//pedimos a react que nos haga un paquete invisible
//LLamamos a nuestro archivo todocounter traer la funcion no el archivo
import React from 'react';
import { AppUI } from './AppUI'

//Ejemplo: tenemos una url 
//const url = "https://.jpg" podemos remplazar con el nombre de la variable

//Esto se traduce a una clase en un div, que solo son llamados a react
//Podemos obtener parametros de elementos desde nuestro index.js observamos que hay buenos
//mensajes existentes

//Lista falsa de todos

// const defultTodos = [
//   {text: 'Cortar cebolla', completed: false},
//   {text: 'Tomar curso intro react', completed: false},
//   {text: 'Llorar con la lloran', completed: false},
//   {text: 'LALALALLALALAA', completed: false},
// ];

function App() {

  //Casi siempre las APIS tienen ciertas versiones
  const localStorageTodos = localStorage.getItem('TODOS_V1');

  //Crear un primer array vacio con cosas vacias, tenbemos que traer la info guardada en local storage
  let parsedTodos;

  //Vemos si tiene informacion
  if(!localStorageTodos){

    //Por defecto una lista de todos
    localStorage.setItem('TODOS_V1',[]);
    parsedTodos = [];
  } 

  //Ya tiene algunos todos
  else{
    parsedTodos = JSON.parse(localStorageTodos);
  }


  const [todos, setTodos] = React.useState(parsedTodos);
  //Creamos nuestros estados a traves de nuestro searchValue y esto hace que haya una posuble reutilizacion de los componentes esto nos ayudara al cambio de estado dinamico
  const [searchValue, setSearchValue] = React.useState('');

  //CUantos todos hemos completado, a traves de nuestro todo viendo la propiedad interesante la forma de validacion con doble signo de admiiracion
  const completedTodos = todos.filter(todo => !!todo.completed).length;
  const totalTodos = todos.length; //Total de todos

  //Buscar si no han escrito nada en un arreglo con el input
  let searchedTodos = [];
  //Vemos si search es mayor que 1 para ver si no han escrito se muetsrasn igual

  /*Otra forma de realizarlo es un poco mas compacto
  const todosFiltered = todos.filter(todo => todo.text.toLowerCase().includes(searchValue.toLowerCase()));
  */

  if(!searchValue.length >= 1){
    searchedTodos = todos;
  } 
  //Vamos a filtrar cantidad de todos en la listay convertimos todas las minisuculas en mayusuclas 
  else {
    //Con esto lo que logramos hacer esque convertimos el input a minusculas de lo que hacemos es llamar a nuestro evento filterdonde buscaremos cada componente rendreizado
  searchedTodos = todos.filter(todo =>{
    const todoText = todo.text.toLowerCase();
    const searchText = searchValue.toLowerCase();
    //De esta forma comparamos con string cuales hay
    return todoText.includes(searchText);
  });
  }

  //Esto es para marcar la funcion como completado, el id es text que ocuparemos
  const completeTodos = (text) =>{
    //Metodo que encuentra el Index
    const todoIndex = todos.findIndex(todo => todo.text === text);

    //Lista de todos entramos y por cada estado de todo Index cambiamos a true

    // todos[todoIndex] = {
    //   text: todos[todoIndex].text,
    //   completed: true,
    // };

    //De esta forma inyectamos todos los todos esto para hacer los rededenres cambiar los estados con lso 3 puntos inyectamos todos
    const newTodos = [...todos];
    //Forma facil de desarrollar los estados clonando todos
    newTodos[todoIndex].completed = true;
    //Causamos el redender con nuestros todos para obtener 
    setTodos(newTodos);
  }


  //Esto para eliminar un arreglo
  const deleteTodos = (text) =>{
    const todoIndex = todos.findIndex(todo => todo.text === text);

    const newTodos = [...todos];
    //El método splice() cambia el contenido de un array eliminando elementos existentes y/o agregando nuevos elementos.
    //En esta caso solo ponemos 1 para un todo eliminado
    newTodos.splice(todoIndex, 1);
    //nuestro render
    setTodos(newTodos);
  }

  return (

    //Creando toda la app desing de la pagina con los props de cada cosa
    <AppUI 
     totalTodos={totalTodos}
     completedTodos={completedTodos}
     searchValue={searchValue}
     setSearchValue={setSearchValue}
     searchedTodos={searchedTodos}
     completeTodos={completeTodos}
     deleteTodos={deleteTodos}
    />

    // //Soporta un componente solamente
    // //Esta etiqueta nos funciona para calculos internos solo enviamos una etiqueta por componente envolver para quitar cantidad divs
    // //Para comentar o poner javascript con las {} claves
    // <React.Fragment>
    // {//Para evitar confusiones no se debe dar class si no que se da con class name evitar clases de js
    // //La magia de JSX es las propiedades que hay
    // <TodoHeader/>
    // //Poner nuestros elementos
    // //Todocounter es el que marcara el numero de todos completados
    // }
    // <TodoCounter
    // total={totalTodos}
    // completed={completedTodos}
    // />

    // {//El input de busqueda que escribamos
    //  //podemos crear a traves de react nuestra app principal los valores de estados de nuestra app
    // }
    // <TodoSearch
    //   searchValue={searchValue}
    //   setSearchValue={setSearchValue}
    // />


    // {//COntenedor de todos con lista
    // //Tenemos nuestro componente que sera el recursivo reutilizable, utilizando map rendrizando
    // }
    // <TodoList>

    // {searchedTodos.map(todo => (
    //       //Tomemos en cuenta que hay que tener un identificador de lista asi como un ID en abse de datos
    //       <TodoItem 
    //       key={todo.text} 
    //       text={todo.text}
    //       //Enviamos una nueva propiedad que se llame completed
    //       completed={todo.completed}
    //       //Enviamos las propiedas a nuestro componente para la actualizacion y re-renderizacion de todos
    //       onComplete={() =>completeTodos(todo.text)}
    //       onDelete={() =>deleteTodos(todo.text)}
    //       />
    //  ))}

    // </TodoList>

    // <CreateTodoButton/>
    // </React.Fragment>
    );


}

//Encapsulamiento javascript
export default App;
